import React, { useState } from "react";
import "./PlanetComponent.css";

const PlanetComponent = (props) => {
  const { name, imageSrc, radiationLevel, soundSrc } = props;
  const [isPlaying, setIsPlaying] = useState(false);

  const toggleSound = () => {
    setIsPlaying(!isPlaying);
    // Add logic here to play/pause sound
    // For example, using <audio> element or any audio library
  };

  return (
    <div className="planet-container">
      {name}
      <img className="planet-image" src="planet_image_url_here" alt="Planet" />
      <p className="description-text">
        Lorem ipsum dolor sit amet, consectetur adipiscing elit. Sed do eiusmod
        tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim
        veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea
        commodo consequat.
      </p>
      <button className="sound-button" onClick={toggleSound}>
        {isPlaying ? "Pause Sound" : "Play Sound"}
      </button>
    </div>
  );
};

export default PlanetComponent;
