import React, { useState } from 'react';

const PlanetComponent = (props) => {
  const [isPlaying, setIsPlaying] = useState(false);

  // Function to toggle sound playing state
  const toggleSound = () => {
    setIsPlaying(!isPlaying);
    // Add logic here to play/pause sound
    // For example, using <audio> element or any audio library
  };

  return (
    <div>
      {/* Image */}
      <img src="planet_image_url_here" alt="Planet" />

      {/* Lorem Ipsum text */}
      <p>
        Lorem ipsum dolor sit amet, consectetur adipiscing elit. Sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat.
      </p>

      {/* Sound play option */}
      <button onClick={toggleSound}>{isPlaying ? 'Pause Sound' : 'Play Sound'}</button>

      {/* Animal photo */}
      <img src="animal_image_url_here" alt="Animal" />

      {/* Animal sound */}
      <audio controls>
        <source src="animal_sound_url_here" type="audio/mpeg" />
        Your browser does not support the audio element.
      </audio>

      {/* Information about the animal */}
      <p>
        Information about the animal and its causes of end, structure, etc.
      </p>
    </div>
  );
};

export default PlanetComponent;
